local common_char = {}
for _, c in ipairs(common_char_) do
	common_char[c] = true
end

local function check(text)
	local bom = utf8.codepoint(text)
	if bom ~= 0xfeff then
		return "Missing BOM"
	end
	text = text:sub(utf8.offset(text, 2))	-- skip bom
	local line = 1
	for _, c in utf8.codes(text) do
		if c < 32 then
			if c == 10 then	-- \n
				line = line + 1
			end
		else
			if not common_char[c] then
				return string.format("Line %d U+%04x [%s]", line, c, utf8.char(c))
			end
		end
	end
end

-- 检查是否是合法的 yml 文件
local function checkyml(text)
	local lno = 1
	local iter = text:gmatch "([^\r\n]*)\r?\n?"
	iter()	-- skip first line
	for line in iter do
		if lno > 1 and not line:match("%s*#") then
			local comment = line:match '([^"]*)$'
			local shape = comment:match "%s*(.)"
			if shape and shape ~= "#" and shape ~= " " then
				return string.format('Line %s missing "', lno)
			end
			line = line:sub(1, #line - #comment)
			if line ~= "" then
				local key, value = line:match ' ([%w%._%-]+:%d) *"(.*)"$'
				if key == nil or value == nil then
					print(line, comment)
					return string.format("Line %s is invalid", lno)
				end
			end
		end
		lno = lno + 1
	end
end

local filename = ...
if filename == nil then
	return function(text)
		return check(text) or checkyml(text)
	end
else
	-- standalone
	local f = assert(io.open((filename)), "Can't open " .. filename)
	local text = f:read "a"
	f:close()
	local err = check(text) or checkyml(text)
	if err then
		print("ERR:", filename, err)
	else
		print("OK:", filename)
	end
end
